        <option key="default">Choose a Product</option>
        {products.map((item, index)=>{
                      return ( 
                <option key={index} value={index} defaultValue={0}
                >{item.name} </option>

                )})}

    </Form.Control>

    </tbody>

  </Form.Group>

  {/* </Col> */}
