import React, { useEffect, useState } from "react";
import { Form, Col, Row, Button, Accordion, Card } from 'react-bootstrap';
// import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import api from '../../utils/api'


function JobForm() {
    const [ products, setProducts ] = useState([])
    const [ product, setProduct ] = useState("")

    // const decoratedOnClick = useAccordionToggle(eventKey, onClick);

    useEffect(() => {
        console.log()
        api.getProducts()
        .then(res => {
          console.log(res.data)
          setProducts(res.data);
        })
        .catch(error => {
          console.log('Error getting fake data: ' + error);
          })

      }, []);  

      function chooseProduct(item) {
          setProduct(item.value)
      }
      console.log(product) 

    return (
        <div>


    <Accordion>
  <Card>
    <Card.Header>
      <Accordion.Toggle as={Button} variant="link" eventKey="0">
        Add Job
      </Accordion.Toggle>
    </Card.Header>
    <Accordion.Collapse eventKey="0">
      <Card.Body>
      <Form>
      <Form.Row>
      <Col>

  <Form.Group controlId="exampleForm.SelectCustom">
    <Form.Label>Custom select</Form.Label>
    <Form.Control as="select" placeholder="Choose a product" custom>
        {products.map((item)=>{
            return (
      <option key={item.id} 
    //   onChange={(e) => setProduct(item)} 
    //   alue={item}
      value={product}
    >{item.name}
    </option>
        )})}
    </Form.Control>
  </Form.Group>
  </Col>
  <Col sm={2}>

<Form.Group controlId="exampleForm.SelectCustom">
  <Form.Label>Quantity</Form.Label>
  <Form.Control placeholder="1" defaultValue="1"/>  
</Form.Group>
</Col>

<Col sm={2}>

<p>Unit Price:</p>
<p> ${product.price}
    </p>
    </Col>

  </Form.Row>
</Form>
          </Card.Body>
    </Accordion.Collapse>
  </Card>
  <Card>
    <Card.Header>
      <Accordion.Toggle as={Button} variant="link" eventKey="1">
        Click me!
      </Accordion.Toggle>
    </Card.Header>
    <Accordion.Collapse eventKey="1">
      <Card.Body>Hello! I'm another body</Card.Body>
    </Accordion.Collapse>
  </Card>
</Accordion>
</div>

    )
}

export default JobForm;
