export const CurrCustProvider = ({ children }) => {
  const [ currCust, setCurrCust ] = useState( {} );
  const [ currJob, setCurrJob ] = useState( {} )
  console.log(currCust)
  console.log(currJob)
//   const [location, setLocation] = useState("Mars");

  return (
    <CurrCustContext.Provider
      value={{
        currCust,
        setCurrCust,
      }}
