import React, { useEffect, useState } from "react";
import { Form, Col, Row, Button, Accordion, Card } from 'react-bootstrap';
// import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import api from '../../utils/api'


function JobForm() {
    const [ products, setProducts ] = useState([])
    const [ price, setPrice ] = useState()
    const [ quantity, setQuantity ] = useState("")
    const [ date, setDate ] = useState("")
    const [ jobType, setJobType ] = useState("") //product group
    const [ jobName, setJobName ] = useState("") //product name
    const [ jobCode, setJobCode ] = useState("")

    let jobID = Math.random()*1000
    console.log(jobID)

    let customerID = 12


    console.log(date)
    // const decoratedOnClick = useAccordionToggle(eventKey, onClick);

    console.log(jobName)

    let total = quantity*price
    let tax = (total*1.06625).toFixed(2)

    function handleSubmit(e) {
        e.preventDefault()
        api.saveJob(jobID, customerID, date, jobType, jobName, jobCode, quantity, price, total)
    }

    useEffect(() => {
        console.log()
        api.getProducts()
        .then(res => {
          console.log(res.data)
          setProducts(res.data);
        })
        .catch(error => {
          console.log('Error getting fake data: ' + error);
          })

      }, []);  

      console.log(price) 
      console.log(typeof(quantity))
      console.log(typeof(price))
      console.log(typeof(total))


    return (
        <div>


    <Accordion>
  <Card>
    <Card.Header>
      <Accordion.Toggle as={Button} variant="link" eventKey="0">
        Add Job
      </Accordion.Toggle>
    </Card.Header>
    <Accordion.Collapse eventKey="0">
      <Card.Body>
      <Form>
      <Form.Row>
      <Col>

  <Form.Group controlId="exampleForm.SelectCustom">
    <Form.Label>Choose Service</Form.Label>

    <Form.Control as="select" custom                
    onChange={(e) => setPrice(parseInt(e.target.value), setJobName(e.target.name))
                    // setProduct(e.options[e.selectedIndex].value
                    }
>
        <option key="default">Choose a Product</option>
        {products.map((item)=>{
            // console.log(item)
            // setJobCode(item.code)
            // console.log(jobCode)
            return (
                <option key={item._id} type={item.productGroup} value={item.price} name={item.name}
                >{item.name} </option>

        )})}
    </Form.Control>
  </Form.Group>
  </Col>
  <Col sm={2}>

<Form.Group controlId="exampleForm.SelectCustom">
  <Form.Label>Quantity</Form.Label>
  <Form.Control placeholder="0" type="text" onChange={(e) => setQuantity(parseInt(e.target.value))} value={quantity}/>  
</Form.Group>
</Col>
<Col>
<p>Unit Price</p>
<p>${price}</p>
</Col>
<Col>
<p>Total</p>
<p>${total.toFixed(2)}</p> 
</Col>
<Col>
<p>With Tax</p>
<p>${tax}</p>
</Col>
<Col>
<p>Date</p>
<input type="date" onChange={(e) => setDate(e.target.value)}></input>
</Col>
<Col>
<button type="submit" onClick={handleSubmit}></button>
</Col>




  </Form.Row>
</Form>
          </Card.Body>
    </Accordion.Collapse>
  </Card>
  <Card>
    <Card.Header>
      <Accordion.Toggle as={Button} variant="link" eventKey="1">
        Click me!
      </Accordion.Toggle>
    </Card.Header>
    <Accordion.Collapse eventKey="1">
      <Card.Body>Hello! I'm another body</Card.Body>
    </Accordion.Collapse>
  </Card>
</Accordion>
</div>

    )
}

export default JobForm;
