import React, { useEffect, useState } from "react";
import { Form, Col, Row, Button, Accordion, Card } from 'react-bootstrap';
// import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';
import api from '../../utils/api'


function JobForm() {
    const [ products, setProducts ] = useState([])
    const [ quantity, setQuantity ] = useState("")
    const [ date, setDate ] = useState("")
    // const [ jobType, setJobType ] = useState({}) //product group
    const [ index, setIndex ] = useState('')
    const [ price, setPrice ] = useState()
    const [ code, setCode ] = useState("")
    const [ product, setProduct ] = useState({})

    let jobID = Math.random()*1000
    console.log(jobID)
    console.log(code)
    var jobType = products[product]
    console.log(products[1])

    console.log(index)

    let customerID = 7832
    console.log(jobType)

    console.log(jobType)
    // console.log(products[index].price)

    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth()+1;
    var yyyy = today.getFullYear();
    if (dd < 10) {
        dd = "0"+dd
    }
    if (mm < 10) {
        mm = "0"+mm
    }
    today = yyyy+"-"+mm+"-"+dd;


    console.log(date)
    // const decoratedOnClick = useAccordionToggle(eventKey, onClick);
    var total = Number


    // setPrice(total)

    // function handleChange = (e) => {
    //     let value = e.target.value
    //     let name = e.target.name
    //     let type = e.target.group
    //    setPrice(parseInt(value),
    // setJobName(name),
    //  setJobType(type)
    // }

    function handleSubmit(e) {
        e.preventDefault()
        api.saveJob(jobID, customerID, date, product, quantity, tax)
    }

    useEffect(() => {
        console.log()
        api.getProducts()
        .then(res => {
          console.log(res.data)
          setProducts(res.data);
        })
        .catch(error => {
          console.log('Error getting fake data: ' + error);
          })

      }, []);  

      console.log(price) 
      console.log(typeof(quantity))
      console.log(typeof(price))
      console.log(typeof(total))

      function handleChange(e) {
        setProduct(products[parseFloat(e.target.value)])

        // setPrice(product.price)


        // setCode(e.target.index)
        // setPrice(e.target.value)
      }
      console.log(typeof index)
      console.log(price)
      useEffect(() => {
        setPrice(product.price)
        setCode(product.code)
      }, [product])
        console.log(price)
         total = quantity*price
        let tax = (total*1.06625).toFixed(2)


    return (
        <div>


    <Accordion>
  <Card>
    <Card.Header>
      <Accordion.Toggle as={Button} variant="link" eventKey="0">
        Add Job
      </Accordion.Toggle>
    </Card.Header>
    <Accordion.Collapse eventKey="0">
      <Card.Body>
      <Form>
      <Form.Row>
      {/* <Col> */}

  <Form.Group controlId="exampleForm.SelectCustom">

<tbody>


    <Form.Control as="select" custom                
    onChange={handleChange} value={index}
                    // setProduct(e.options[e.selectedIndex].value   (e) => {setIndex(e.target.value)}

>    <Form.Label>Choose Service</Form.Label>

        <option key="default">Choose a Product</option>
        {products.map((item, index)=>{
                      return ( 
                <option key={index} value={index} 
                >{item.name} </option>

                )})}

    </Form.Control>

    </tbody>

  </Form.Group>

  {/* </Col> */}
  <Col sm={2}>

<Form.Group controlId="exampleForm.SelectCustom">
  <Form.Label>Quantity</Form.Label>
  <Form.Control defaultValue={1} onChange={(e) => setQuantity(parseInt(e.target.value))} value={quantity} />
</Form.Group>
</Col>
<Col>
<p>Unit Price</p>
<p>${price}</p>
</Col>
<Col>
<p>Total</p>

     <p>${total.toFixed(2)}</p> 
     {/* <p></p>} */}
</Col>
<Col>
<p>With Tax</p>
<p>${tax}</p>
</Col>
<Col>
<p>Date</p>
<input type="date" min={today}onChange={(e) => setDate(e.target.value)}></input>
</Col>
<Col>
<Button variant="success" type="submit" onClick={handleSubmit}>Confirm</Button>
</Col> 




  </Form.Row>
</Form>
          </Card.Body>
    </Accordion.Collapse>
  </Card>
  <Card>
    <Card.Header>
      <Accordion.Toggle as={Button} variant="link" eventKey="1">
        Click me!
      </Accordion.Toggle>
    </Card.Header>
    <Accordion.Collapse eventKey="1">
      <Card.Body>Hello! I'm another body</Card.Body>
    </Accordion.Collapse>
  </Card>
</Accordion>
</div>

    )
}

export default JobForm;
