import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table'
import api from  '../../utils/api'
import { Form, Col, Row, Button } from 'react-bootstrap';

function CustomerTable() {
    const [allData,setAllData] = useState([]);
    const [ upholstery, setUpholstery ] = useState([]);
    const [ carpet, setCarpet ] = useState([]);


    useEffect(() => {
        console.log()
        api.getCustomers()
        .then(res => {
          console.log(res.data)
          setAllData(res.data);
          console.log(allData)
        })
        .catch(error => {
          console.log('Error getting fake data: ' + error);
          })

      }, []);
      console.log(allData)

      function onDelete(e) {
        api.deleteProduct()
    }


    return ( 

<Table striped bordered hover size="sm">
  <thead>
    <tr>
      <th>Customer ID</th>
      <th>Customer Name</th>
      <th>Cell Phone</th>
      <th>Home Phone</th>
      <th>Email</th>
      <th>Address</th>
      <th>Jobs</th>

    </tr>
  </thead>
  <tbody>
      {allData.map((data)=> {
                    return (

                // (data.group === "Upholstery") ?

            <tr key={data._id}>
            <td>{data.customerID}</td>
            <td>{data.lastName}, {data.firstName}</td>
            <td>${data.cellPhone}</td>
            <td>{data.homePhone}</td>
            <td>{data.email}</td>
            <td>${data.address.city}</td>
            <td>{data.jobs}</td>

            <td><Button size="sm" variant="info">Update</Button>
            <Button onClick={onDelete} style={{marginLeft: "10px"}} size="sm" variant="danger">Delete</Button></td>
          </tr>
    //   : <tr></tr>
          ) 
      })}

  </tbody>
</Table>  
    )
}

export default CustomerTable;
