      <Card.Body>
      <Form>
      <Form.Row>
      <Col>

  <Form.Group controlId="exampleForm.SelectCustom">
    <Form.Label>Choose Service</Form.Label>
    <tbody>

    <Form.Control as="select" custom                
    onChange={(e) => {setIndex(e.target.value)}}
                    // setProduct(e.options[e.selectedIndex].value

>
        <option key="default">Choose a Product</option>
        {products.map((item, index)=>{
            // console.log(item.group)
            // setJobCode(item.code)
            // console.log(jobCode)
            return (
                <option key={item._id} value={index} 
                >{item.name} </option>

        )})}
    </Form.Control>
    <td>${price}</td>
    <td>${total.toFixed(2)}</td>
    <td>${tax}</td>
    <td><input type="date" min={today}onChange={(e) => setDate(e.target.value)}></input></td>
    <td><button type="submit" onClick={handleSubmit}></button></td>
    </tbody>
  </Form.Group>
  </Col>
  {/* <Col sm={2}> */}

